// src/components/GoalList.js
import React from 'react';
import GoalCard from './GoalCard';
import './GoalList.css';

// Accept onUpdateGoal prop
function GoalList({ goals, onUpdateGoal }) { // Add onUpdateGoal to props
  return (
    <div className="goal-list">
      {goals.map(goal => (
        // Pass onUpdateGoal to each GoalCard
        <GoalCard key={goal.id} goal={goal} onUpdateGoal={onUpdateGoal} />
      ))}
    </div>
  );
}

export default GoalList;
