// src/components/GoalCard.js
import React, { useState } from 'react'; // Import useState for local state
import './GoalCard.css';

// Accept onUpdateGoal prop
function GoalCard({ goal, onUpdateGoal }) { // Add onUpdateGoal to props
  const [depositAmount, setDepositAmount] = useState(''); // Local state for deposit input

  // Calculate progress percentage
  const progress = (goal.savedAmount / goal.targetAmount) * 100;
  const progressBarWidth = Math.min(progress, 100); // Cap at 100%

  // Calculate remaining amount
  const remainingAmount = goal.targetAmount - goal.savedAmount;

  // Function to format currency (e.g., $1,234.56)
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  // Helper for deadline status
  const getDeadlineStatus = (deadlineDate) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Normalize today to start of day
    const deadline = new Date(deadlineDate);
    deadline.setHours(0, 0, 0, 0); // Normalize deadline to start of day

    const diffTime = deadline.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (goal.savedAmount >= goal.targetAmount) {
      return <span className="status completed">Completed!</span>;
    } else if (diffDays <= 0) { // Deadline has passed and not completed
      return <span className="status overdue">Overdue</span>;
    } else if (diffDays <= 30) { // Deadline within 30 days and not completed
      return <span className="status warning">Due Soon ({diffDays} days left)</span>;
    }
    return <span className="status on-track">{diffDays} days left</span>;
  };

  // --- NEW: Handle Deposit ---
  const handleDeposit = () => {
    const amount = parseFloat(depositAmount);

    if (isNaN(amount) || amount <= 0) {
      alert("Please enter a valid positive deposit amount.");
      return;
    }

    // Calculate the new saved amount
    const newSavedAmount = goal.savedAmount + amount;

    // Call the onUpdateGoal function passed from App.js
    // App.js will handle the PATCH request and state update
    onUpdateGoal(goal.id, { savedAmount: newSavedAmount });

    // Clear the deposit input field
    setDepositAmount('');
  };


  return (
    <div className="goal-card">
      <h3>{goal.name}</h3>
      <p>Category: {goal.category}</p>
      <p>Target: {formatCurrency(goal.targetAmount)}</p>
      <p>Saved: {formatCurrency(goal.savedAmount)}</p>
      <p>Remaining: {formatCurrency(remainingAmount)}</p>
      <p>Deadline: {goal.deadline} {getDeadlineStatus(goal.deadline)}</p>

      {/* Progress Bar */}
      <div className="progress-bar-container">
        <div 
          className="progress-bar-fill" 
          style={{ width: `${progressBarWidth}%`, backgroundColor: progress >= 100 ? 'green' : '#4CAF50' }}
        ></div>
      </div>
      <p className="progress-percentage">{progress.toFixed(2)}%</p>

      {/* Action Buttons */}
      <div className="card-actions">
        {/* NEW Deposit Input and Button */}
        <div className="deposit-section">
          <input
            type="number"
            placeholder="Deposit amount"
            value={depositAmount}
            onChange={(e) => setDepositAmount(e.target.value)}
            min="0"
            step="0.01"
            className="deposit-input"
          />
          <button className="deposit-btn" onClick={handleDeposit}>Deposit</button>
        </div>

        <button className="edit-btn">Edit</button> {/* Will be implemented later */}
        <button className="delete-btn">Delete</button> {/* Will be implemented later */}
      </div>
    </div>
  );
}

export default GoalCard;
