// src/App.js
import React, { useState, useEffect } from 'react';
import GoalList from './components/GoalList';
import AddGoalForm from './components/AddGoalForm'; // Import the new component
import './App.css'; 

const API_BASE_URL = 'http://localhost:3000'; 

function App() {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to fetch goals (can be reused if needed)
  const fetchGoals = () => {
    setLoading(true);
    setError(null);
    fetch(`${API_BASE_URL}/goals`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setGoals(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching goals:", err);
        setError("Failed to load goals. Please try again later.");
        setLoading(false);
      });
  };

  // Initial fetch when the component mounts
  useEffect(() => {
    fetchGoals();
  }, []); 

  // Function to handle adding a new goal
  const handleAddGoal = (newGoalData) => {
    fetch(`${API_BASE_URL}/goals`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newGoalData),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(addedGoal => {
      // Update the goals state to include the newly added goal
      setGoals(prevGoals => [...prevGoals, addedGoal]);
      alert('Goal added successfully!'); // User feedback
    })
    .catch(err => {
      console.error("Error adding goal:", err);
      alert('Failed to add goal: ' + err.message); // User feedback
    });
  };

  if (loading) {
    return <div className="loading">Loading goals...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="App">
      <header>
        <h1>Smart Goal Planner</h1>
      </header>
      <main>
        {/* Placeholder for Overview Dashboard */}
        <section className="overview-dashboard">
          <h2>Overview</h2>
          {/* We'll calculate and display overview stats here later */}
          <p>Total Goals: {goals.length}</p>
          {/* Other overview stats */}
        </section>

        {/* Add Goal Form - Now rendered here */}
        <section className="add-goal-section">
          <h2>Add New Goal</h2>
          <AddGoalForm onAddGoal={handleAddGoal} /> {/* Pass the handler function */}
        </section>

        {/* Goal List */}
        <section className="goal-list-section">
          <h2>Your Goals</h2>
          <GoalList goals={goals} /> {/* Pass goals down to GoalList */}
        </section>
      </main>
    </div>
  );
}

export default App;
